require_relative '../roman_numeral'
describe RomanNumeral do
  {1 => 'I', 2 => 'II', 3 => 'III', 4 => 'IV', 5 => 'V', 6 => 'VI', 7 => 'VII', 8 => 'VIII', 9 => 'IX', 10 => 'X', 11 => 'XI', 12 => 'XII', 13 => 'XIII', 14 => 'XIV', 15 => 'XV', 16 => 'XVI', 17 => 'XVII', 18 => 'XVIII', 19 => 'XIX', 20 => 'XX', 21 => 'XXI', 50 => 'L', 51 => 'LI', 55 => 'LV', 59 => 'LIX', 60 => 'LX', 61 => 'LXI', 99 => 'XCIX', 100 => 'C', 151 => 'CLI', 179 => 'CLXXIX', 499 => 'CDXCIX', 500 => 'D', 689 => 'DCLXXXIX', 873 => 'DCCCLXXIII', 999 => 'CMXCIX', 1000 => 'M', 1001 => 'MI', 1664 => 'MDCLXIV', 2000 => 'MM', 4999 => 'MMMMCMXCIX', 5000 => 'MMMMM'}.each do |k,v|
    it "returns #{v}" do
      expect(k.to_roman_numeral).to eql v
    end
  end


  context 'when order magnitude is too high (overflow) and overflow is false' do
    [10000, 100000, 1000000].each do |k|
      it 'raises a ArgumentError' do
        expect {k.to_roman_numeral}.to raise_error(ArgumentError)
      end
    end
  end

  context 'when order magnitude is too high (overflow) and overflow is true' do
    {10000 => 'MMMMMMMMMM', 100000 => 'MMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMM', 1000000 => ''}.each do |k, v|
      it "returns #{v}" do
        expect(k.to_roman_numeral(overflow: true)).to eql v
      end
    end
  end
end
